package Java;

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//looping means to continue
		
		//purpose of this program is to print all the value i beginning with 0 and 20  
		
		for(int i=0;   //started the value i =0
				i<30;  //condition
				i+=3)
		{
		    System.out.println(i);
			
			
			
		}
			
			
			
			
		
	}

}
